mapper.select:
select t.*,t1.supplier_name as supplierName,t2.note as orderStatusNote, t3.note as sendTypeNote, t4.contract_name as contractName, t5.note as payTypeNote, t6.note orderClassName, t7.materialName, t92.opt_button as checkUserButton,t93.create_user_button as createUserButton, t92.role_code as checkRoleCode from t_pss_order t left join t_pss_supplier t1 on t.supplier_code = t1.supplier_code left join t_pss_dict t2 on t.status = t2.code and t2.type = 'ddzt' left join t_pss_dict t3 on t.send_type = t3.code and t3.type = 'sdfs' left join t_pss_contract_purchase t4 on t.contract_code_p = t4.contract_code left join t_pss_dict t5 on t.pay_type = t5.code and t5.type = 'fkfs' left join t_pss_dict t6 on t.order_class = t6.code and t6.type = 'ddxl' left join (select foreign_code,GROUP_CONCAT(material_name) materialName from t_pss_material_detail_log group by foreign_code) t7 on t.order_code = t7.foreign_code left join t_pss_process_task t91 on t.process_id = t91.process_id left join t_pss_process t92 on t91.step = t92.step and t92.type = 'cgdd' left join t_pss_status_button t93 on t93.`status` = t.`status` and t93.type ='ddzt' where 1=1
and t.status = #{orderDto.status}
and t.order_type = #{orderDto.orderType}
and t.order_class = #{orderDto.orderClass}
and t.order_code = #{orderDto.orderCode}
and t7.materialName like "%"#{orderDto.materialName}"%"
and t4.contract_name like "%"#{orderDto.contractName}"%"
and t.send_type = #{orderDto.sendType}
and t.id = #{orderDto.id}
and t.application_date between #{orderDto.appBeginDate} and #{orderDto.appEndDate}
order by t.id desc

select t.*,t1.supplier_name as supplierName,t2.note as orderStatusNote, t3.note as sendTypeNote, t4.contract_name as contractName, t5.note as payTypeNote, t6.note orderClassName, t7.materialName, t92.opt_button as checkUserButton,t93.create_user_button as createUserButton, t92.role_code as checkRoleCode from t_pss_order t left join t_pss_supplier t1 on t.supplier_code = t1.supplier_code left join t_pss_dict t2 on t.status = t2.code and t2.type = 'ddzt' left join t_pss_dict t3 on t.send_type = t3.code and t3.type = 'sdfs' left join t_pss_contract_purchase t4 on t.contract_code_p = t4.contract_code left join t_pss_dict t5 on t.pay_type = t5.code and t5.type = 'fkfs' left join t_pss_dict t6 on t.order_class = t6.code and t6.type = 'ddxl' left join (select foreign_code,GROUP_CONCAT(material_name) materialName from t_pss_material_detail_log group by foreign_code) t7 on t.order_code = t7.foreign_code left join t_pss_process_task t91 on t.process_id = t91.process_id left join t_pss_process t92 on t91.step = t92.step and t92.type = 'ckd' left join t_pss_status_button t93 on t93.`status` = t.`status` and t93.type ='ddzt' where 1=1
and t.status = #{orderDto.status}
and t.order_type = #{orderDto.orderType}
and t.order_class = #{orderDto.orderClass}
and t.order_code = #{orderDto.orderCode}
and t7.materialName like "%"#{orderDto.materialName}"%"
