mapper.select:
select t.*,GROUP_CONCAT(t2.role_code) as roleCodes,GROUP_CONCAT(t3.name) as roleNames from t_pss_user t left join t_pss_user_role t2 on t.user_id = t2.user_id
left join t_pss_role t3 on t2.role_code = t3.code
where 1=1

    and t.name like "%"#{user.name}"%"


    and t.is_delete = #{user.isDelete}


    and t.mobile = #{user.mobile}


    and t.dept_code = #{user.deptCode}

group by t.id,t.user_id,t.name,t.mobile,t.is_delete,t.remark,t.dept_code

select A.*, GROUP_CONCAT(B.role_code) AS roleCodes, GROUP_CONCAT(C.NAME) AS roleNames from ( SELECT t.*,t1.name deptName from t_pss_user t left join t_pss_dept t1 on t.dept_code = t1.dept_code where t.user_type ='1' union ALL SELECT t.*,t1.cust_name deptName from t_pss_user t left join t_pss_customer t1 on t.dept_code = t1.cust_code where t.user_type ='2' union ALL SELECT t.*,t1.supplier_name deptName from t_pss_user t left join t_pss_supplier t1 on t.dept_code = t1.supplier_code where t.user_type ='3' ) A LEFT JOIN t_pss_user_role B ON A.user_id = B.user_id LEFT JOIN t_pss_role C ON B.role_code = C.CODE WHERE 1 = 1
and A.user_id = #{user.userId}
group by A.id,A.user_id,A.USER_TYPE,A.mobile,A.name,A.PASSWORD,A.is_delete,A.remark,A.dept_code,A.deptName

select (
select count(*) from t_pss_contract_sale where contract_status = '4'
) contractSaleCount,
(
select count(*)  from t_pss_contract_purchase where contract_status = '4'
) contractPurCount,
(
select count(*) from t_pss_order where order_type = '1' and  status !='1'
) orderPurCount,
(
select count(*) from t_pss_order where order_type = '1' and  status !='1' AND left(application_date,4) = DATE_FORMAT(NOW(),'%Y')
) orderPurCountYear,
(
select count(*) from t_pss_order where order_type = '1' and  status !='1' AND application_date = DATE_FORMAT(NOW(),'%Y-%m-%d')
) orderPurCountToday,
(
select count(*) from t_pss_order where order_type = '1' and  status = '7'
) orderPurCountFinish,
(
select count(*) from t_pss_order where order_type = '1' and  status = '2'
) orderPurCountCheck,
(
select count(*) from t_pss_order where order_type = '1' and  status = '3'
) orderPurCountToSend,
(
select count(*) from t_pss_user where user_type = '1'
) userCount,
(
select count(*) from t_pss_supplier
) supplierCount,
(
select count(*) from t_pss_customer
) customerCount from dual
